module.exports = function(options, app){

var hook = require('hooks-node');

function before(args){
	var fs = require('fs'), localpath = app.get('views')+'/'+args[0];
	if(fs.existsSync(localpath)) return;
	
	var remotepath = args[0].split('.')[1] + '/' + args[0];
	var url = 'https://raw.github.com/dpweb/common/master/'+remotepath;
	require('request')(url, function(e,r,b){
					if(!e && r.statusCode==200) {				
							fs.writeFile(localpath, b, function(e,r){});
					}
	})
}
hook(before, app, 'render', function(){});
}